function display_secondary() {
    echo output_secondary();
}

function output_secondary() {
    if (has_nav_menu('secondary')) { ?>
        <div class="main-navigation">
            <nav class="secondary-navigation">
                <?php wp_nav_menu( array(
                    'theme_location'    => 'secondary',
                )); 
                ?>
            </nav>
        </div>
    <?php }
}